import React, { useEffect, useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import GetCharacters from './GetCharacters';
import OtherFunctionalities from './OtherFunctionalities';
import DeleteCharacterButton from './DeleteCharacterButton';
import EpisodeList from './EpisodeList';

function App() {
  const [episodeData, setEpisodeData] = useState([]);
  const [locationData, setLocationData] = useState([]);
  const [characterData, setCharacterData] = useState([]);

  useEffect(() => {
    fetch('https://rickandmortyapi.com/api/episode')
      .then((resp) => resp.json())
      .then((data) => {
        setEpisodeData(data.results);
      });

    fetch('https://rickandmortyapi.com/api/location')
      .then((resp) => resp.json())
      .then((data) => {
        setLocationData(data.results);
      });

    fetch('https://rickandmortyapi.com/api/character')
      .then((resp) => resp.json())
      .then((data) => {
        setCharacterData(data.results);
      });
  }, []);

  const handleDeleteCharacter = (characterId) => {
    const updatedCharacterData = characterData.filter(
      (character) => character.id !== characterId
    );
    setCharacterData(updatedCharacterData);
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/other-functionalities">Other Functionalities</Link>
            </li>
            <li>
              <Link to="/episodes">Episodes</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<Home characterData={characterData} />} />
          <Route path="/other-functionalities" element={<OtherFunctionalities />} />
          <Route path="/episodes" element={<EpisodeList episodeData={episodeData} />} />
        </Routes>

        <div className="location-section">
          <h2>Locations:</h2>
          <ul className="location-list">
            {locationData.map((location) => (
              <li key={location.id} className="location-item">
                {location.name} - {location.type}
              </li>
            ))}
          </ul>
        </div>

        <DeleteCharacterButton onDeleteCharacter={handleDeleteCharacter} />

        <div className="episode-section">
          <h2>Episodes:</h2>
          <ul className="episode-list">
            {episodeData.map((episode) => (
              <li key={episode.id} className="episode-item">
                <span className="episode-icon">ðŸ“º</span>
                {episode.name} - {episode.characters.join(', ')}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </Router>
  );
}

function Home({ characterData }) {
  return (
    <>
      <h1>My Rick and Morty App</h1>
      <GetCharacters characterData={characterData} />
    </>
  );
}

export default App;
