import React, { useState } from 'react';
import './DeleteCharacterButton.css';

function DeleteCharacterButton({ onDeleteCharacter }) {
  const [characterId, setCharacterId] = useState('');

  const handleDeleteCharacter = (e) => {
    e.preventDefault();

    // Send a DELETE request to the backend to delete the character
    fetch(`http://localhost:3000/results/${characterId}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to delete character.');
        }
        return response.json();
      })
      .then((data) => {
        // Notify the parent component that the character has been deleted
        onDeleteCharacter(characterId);
        setCharacterId(''); // Reset the form field after deletion
      })
      .catch((error) => {
        console.error('Error deleting character:', error);
        alert('Failed to delete character. Please try again later.');
      });
  };

  return (
    <div className="delete-character-container">
      <form onSubmit={handleDeleteCharacter} className="delete-character-form">
        <input
          type="text"
          placeholder="Character ID"
          value={characterId}
          onChange={(e) => setCharacterId(e.target.value)}
        />
        <button type="submit" className="delete-button">
          Delete Character
        </button>
      </form>
    </div>
  );
}

export default DeleteCharacterButton;
