import React from 'react';
import './DeleteCharacterButton.css';

function DeleteCharacterButton({ characterId, characterName, onDeleteCharacter }) {
  const handleDeleteCharacter = () => {
    // Send a DELETE request to the backend to delete the character
    fetch(`http://localhost:3000/results/${characterId}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to delete character.');
        }
        return response.json();
      })
      .then(() => {
        // Notify the parent component that the character has been deleted
        onDeleteCharacter(characterId);
      })
      .catch((error) => {
        console.error('Error deleting character:', error);
        alert('Failed to delete character. Please try again later.');
      });
  };

  const handlePatchCharacter = () => {
    // Send a PATCH request to the backend to update the character
    fetch(`http://localhost:3000/results/${characterId}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name: `${characterName} (Updated)`,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to update character.');
        }
        return response.json();
      })
      .then(() => {
        // Notify the parent component that the character has been updated
        // We're not changing the characterId or characterName here, so no need to pass them.
        alert('Character updated successfully.');
      })
      .catch((error) => {
        console.error('Error updating character:', error);
        alert('Failed to update character. Please try again later.');
      });
  };

  return (
    <div className="delete-character-container">
      <div className="character-name">{characterName}</div>
      <button type="button" className="delete-button" onClick={handleDeleteCharacter}>
        Delete Character
      </button>
      <button type="button" className="update-button" onClick={handlePatchCharacter}>
        Update Character
      </button>
    </div>
  );
}

export default DeleteCharacterButton;
