import React, { useState } from 'react';
import './DeleteCharacterButton.css';

function DeleteCharacterButton({ onDeleteCharacter }) {
  const [characterId, setCharacterId] = useState('');
  const [characterName, setCharacterName] = useState('');

  const handleDeleteCharacter = (e) => {
    e.preventDefault();

    // Send a DELETE request to the backend to delete the character
    fetch(`http://localhost:3000/results/${characterId}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to delete character.');
        }
        return response.json();
      })
      .then((data) => {
        // Notify the parent component that the character has been deleted
        onDeleteCharacter(characterId);
        setCharacterId(''); // Reset the form field after deletion
      })
      .catch((error) => {
        console.error('Error deleting character:', error);
        alert('Failed to delete character. Please try again later.');
      });
  };

  const handleUpdateCharacter = (e) => {
    e.preventDefault();

    // Send a PATCH request to the backend to update the character
    fetch(`http://localhost:3000/results/${characterId}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ name: characterName }), // Assuming you want to update the character's name
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to update character.');
        }
        return response.json();
      })
      .then((data) => {
        // Handle the updated character data (if needed)
        console.log('Character updated:', data);
        // Reset the form field after updating
        setCharacterName('');
      })
      .catch((error) => {
        console.error('Error updating character:', error);
        alert('Failed to update character. Please try again later.');
      });
  };

  return (
    <div className="delete-character-container">
      <form onSubmit={handleDeleteCharacter} className="delete-character-form">
        <input
          type="text"
          placeholder="Character ID"
          value={characterId}
          onChange={(e) => setCharacterId(e.target.value)}
        />
        <button type="submit" className="delete-button">
          Delete Character
        </button>
      </form>

      <form onSubmit={handleUpdateCharacter} className="update-character-form">
        <input
          type="text"
          placeholder="New Character Name"
          value={characterName}
          onChange={(e) => setCharacterName(e.target.value)}
        />
        <button type="submit" className="update-button">
          Update Character Name
        </button>
      </form>
    </div>
  );
}

export default DeleteCharacterButton;
